var build = require("fuse-box");

let fuse = build.FuseBox.init({
    outFile: "./dist/@angular.js",
    // TEMP, due to a fusebox bug
    cache: false
});

// Bundle and exec index.js
fuse.bundle("> ./index.js", () => {

    // Minify it
    let fuseMin = build.FuseBox.init({
        outFile: "./dist/@angular.min.js",
        // TEMP, due to a fusebox bug
        cache: false,
        plugins: [
            build.UglifyJSPlugin()
        ]
    });

    // Bundle and exec index.js
    fuseMin.bundle("> ./index.js");
});

